mapper.delete:
DELETE FROM file_info
WHERE file_pid LIKE CONCAT(#{filePid}, '%')
  AND user_id = #{userId}

DELETE FROM file_info
WHERE file_id = #{fileId}
  AND user_id = #{userId}

mapper.select:
select *
from file_info


        AND user_id = #{userId}


        AND file_pid = #{filePid}


        AND file_name = #{fileName}


        AND folder_type = #{folderType}

select *
from file_info
where file_md5=#{fileMd5} and folder_type=0 limit 1;

select *
from file_info


        AND user_id = #{userId}


        AND file_id = #{fileId}

select *
from file_info


        AND user_id = #{userId}


        AND file_pid = #{filePid}


        AND file_id = #{fileId}


        AND folder_type = #{folderType}

SELECT * FROM file_info
WHERE user_id = #{userId} AND file_pid = #{filePid}
ORDER BY folder_type DESC, last_update_time DESC
    LIMIT #{page}, #{pageSize}
